INSERT INTO quiz_quizzes (id, name, description, level)
VALUES (8, 'Легенды Рока',
        '"Легенды Рока" - это захватывающий музыкальный квиз, который исследует историю и звуки рок-музыки через времена. Пройдите через эпохи и стили, от классического рока до современных хитов, и протестируйте свои познания в этой захватывающей игре! Покажите свою любовь к музыке и станьте настоящим знатоком рок-легенд.',
        'Easy'),
       (7, 'РУССКИЙ РОК VS ЗАРУБЕЖНЫЙ РОК',
        'Добро пожаловать в увлекательный квиз "РУССКИЙ РОК VS ЗАРУБЕЖНЫЙ РОК"! Этот квиз погрузит вас в мир рок-музыки из разных уголков мира. Вы будете отвечать на вопросы, сравнивая хиты и исполнителей русского рока с зарубежными коллегами. Приготовьтесь к захватывающему соревнованию знаний и окунитесь в атмосферу звуков рока, наслаждаясь музыкой и конкуренцией!',
        'Easy'),
       (6, '10e vs 20e',
        'Добро пожаловать на музыкальный квиз "2010 vs 2020"! Этот захватывающий квиз приглашает вас на увлекательное музыкальное путешествие через два десятилетия - 2010-е и 2020-е годы. Проявите свои знания музыкальной индустрии, сравнивая хиты, артистов и тренды этих двух эпох. Готовы ли вы выявить, как изменилась музыка за это время? Пригласите друзей, соберитесь вместе и проверьте свою музыкальную эрудицию в "2010 vs 2020"!',
        'Easy'),
       (5, 'Олды: русский рок vs русская попса',
        'Добро пожаловать на музыкальный квиз "Олды: русский рок vs русская попса"! Готовы окунуться в ностальгию и проверить свои знания о русской музыке прошлых десятилетий? Этот квиз позволит вам сразиться в забавных и увлекательных викторинах, где вам предстоит отличить хиты русского рока от поп-хитов. Вспомните легендарные группы и исполнителей, сравните их хиты и попытайтесь угадать, кто стал символом своего жанра! Готовы к музыкальному вызову? Давайте начнем!',
        'Hard'),
       (4, 'MEN''S EDITION',
        '"MEN''S EDITION" - увлекательный музыкальный квиз, специально созданный для представителей сильного пола! Подготовьтесь к тому, чтобы продемонстрировать свои знания в области музыки, от классических хитов до современных треков. Пройдите через различные раунды викторины, где вам предстоит угадывать исполнителей, называть песни по первой строчке и многое другое. Докажите свою музыкальную эрудицию в "MEN''S EDITION" и станьте королем музыкальных квизов!',
        'Easy'),
       (3, 'GIRL''S EDITION',
        'Добро пожаловать в музыкальный квиз "Girl''s Edition"! Этот квиз представляет собой захватывающее музыкальное путешествие, посвященное исключительно женским исполнительницам и их хитам. Приготовьтесь к увлекательным вопросам о музыке, которую любят и ценят миллионы, и испытайте свои знания относительно легендарных женских треков. Покажите всем, насколько вы хорошо знаете музыку и пройдите через этот захватывающий музыкальный квиз "Girl''s Edition"!',
        'Easy'),
       (2, 'Верните мой 2007',
        'Добро пожаловать на музыкальный квиз "Верните мой 2007"! Этот увлекательный музыкальный квиз призван вернуть вас в год 2007, наполненный хитами и запоминающимися мелодиями. Пройдите через волну ностальгии и проверьте свою память на знание музыкальных треков этого удивительного года. Готовы ли вы вспомнить, какие песни стояли на вершине хит-парадов и чем запомнился этот музыкальный период? Присоединяйтесь к нам на "Верните мой 2007" и окунитесь в атмосферу золотой эпохи музыки!',
        'Easy');
